import { getToken } from "./userSession";

const baseURL = process.env.REACT_APP_SERVER_URL;

export const fetchCharacters = async () => {
  const token = getToken();
  const characterResponse = await fetch(`${baseURL}/characters`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });

  const dataCharacters = await characterResponse.json();

  return dataCharacters;
};

export const fetchFavorites = async () => {
  const token = getToken();
  const favoritesResponse = await fetch(`${baseURL}/favorites`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });

  const dataFavorites = await favoritesResponse.json();

  return dataFavorites;
};

export const login = async ({ username, password }) => {
  const favoritesResponse = await fetch(`${baseURL}/login`, {
    method: "POST",

    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ username, password }),
  });

