import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import RickandMorty from "../images/rick-and-morty.png";
import ErrorMessage from "./ErrorMessage";

const SignUp = () => {
  const navigate = useNavigate();
  const [signUpInfo, setSignUpInfo] = useState({
    username: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState("");
  const handleChange = (event) => {
    setSignUpInfo((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  };

  const signUpUser = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER_URL}/signup`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(signUpInfo),
        }
      );
      const data = await response.json();

      if (response.ok) {
        console.log("User created successfully. You can now see Pickle Rick!!");

        if (data.token) {
          localStorage.setItem("token", data.token);
        }
        navigate("/characters");
        setSignUpInfo({ username: "", password: "" });
      } else {
        setErrorMessage(
          data.message || "Sign failed. Please check your details."
        );
        setSignUpInfo({ username: "", password: "" });
      }
    } catch (error) {
      console.error("Sign up error:", error);
    }
  };

  return (
    <div style={styles.container}>
      <img style={styles.image} alt="rick and morty wiki" src={RickandMorty} />
      <h1 style={styles.header}>To see Pickle Rick you need to sign up</h1>

      <form style={styles.form} onSubmit={signUpUser}>
        <input
          name="username"
          placeholder="Username"
          value={signUpInfo.username}
          onChange={handleChange}
          style={styles.input}
          required
        />
        <input
          name="password"
          placeholder="Password"
          value={signUpInfo.password}
          type="password"
          onChange={handleChange}
          style={styles.input}
          required
        />
        {errorMessage && <ErrorMessage error={errorMessage} />}
        <button style={styles.button} type="submit">
          Sign Up
        </button>
      </form>

      <p style={styles.redirect}>
        Have an account? Go to
        <span
          style={styles.link}
          onClick={() => {
            navigate("/login");
          }}
        >
          Login
        </span>
      </p>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  image: {
    maxWidth: "90%",
    height: "auto",
    marginBottom: "20px",
  },
  header: {
    color: "#fff",
  },
  form: {
    padding: "30px",
    borderRadius: "12px",
    boxShadow: "0 0 10px #02afc5",
    display: "flex",
    flexDirection: "column",
    gap: "15px",
    width: "100%",
    maxWidth: "400px",
  },
  input: {
    padding: "12px",
    borderRadius: "8px",
    border: "2px solid #02afc5",
    backgroundColor: "#101010",
    color: "#fff",
    fontSize: "16px",
  },
  redirect: {
    marginTop: "20px",
    color: "#02afc5",
    fontSize: "16px",
  },
  link: {
    color: "#02afc5",
    textDecoration: "underline",
    cursor: "pointer",
    fontWeight: "bold",
    marginLeft: "5px",
  },
  button: {
    backgroundColor: "#02afc5",
    color: "#fff",
    padding: "12px",
    borderRadius: "8px",
    border: "none",
    fontWeight: "bold",
    fontSize: "16px",
    cursor: "pointer",
    transition: "background 0.2s ease-in-out",
  },
};

export default SignUp;
