import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import RickandMorty from "../images/rick-and-morty.png";
import CharacterTile from "./CharacterTile";
import Pagination from "./Pagination";

const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const apiURL = `${process.env.REACT_APP_SERVER_URL}/characters/?page=${pageNumber}`;

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        const [characterResponse, favoritesResponse] = await Promise.all([
          fetch(apiURL, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
          fetch(`${process.env.REACT_APP_SERVER_URL}/favorites`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
        ]);

        const dataCharacters = await characterResponse.json();
        const dataFavorites = await favoritesResponse.json();
        setCharacters(dataCharacters.characters);
        setTotalPages(dataCharacters.info.pages);
        setFavorites(dataFavorites.favorites);
      } catch (error) {
        console.log("There has been an error");
      }
    };

    fetchData();
  }, [pageNumber, token]);

  const handleFavorites = async (id) => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER_URL}/favorites/${id}`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();

      setFavorites((prev) =>
        prev.includes(id) ? prev.filter((favId) => favId !== id) : [...prev, id]
      );
    } catch (error) {
      console.error("Failed to toggle favorite", error);
    }
  };

  return (
    <div style={styles.container}>
      <img style={styles.image} alt="rick and morty wiki" src={RickandMorty} />
      <button
        style={styles.logoutButton}
        onClick={() => {
          localStorage.removeItem("token");
          navigate("/login");
        }}
      >
        Logout
      </button>
      <div style={styles.list}>
        {characters?.map((character, index) => (
          <CharacterTile
            character={character}
            key={index}
            isFavorite={favorites.includes(character.id)}
            handleFavorites={() => handleFavorites(character.id)}
          />
        ))}
      </div>
      <Pagination
        pageNumber={pageNumber}
        totalPages={totalPages}
        setPageNumber={setPageNumber}
      />
    </div>
  );
};
const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    margin: "20px",
  },
  image: {
    maxWidth: "90%",
    height: "auto",
    marginBottom: "20px",
  },
  list: {
    display: "flex",
    flexWrap: "wrap",
    gap: "20px",
    justifyContent: "center",
  },
  logoutButton: {
    position: "absolute",
    top: "20px",
    right: "20px",
    backgroundColor: "#02afc5",
    color: "#fff",
    padding: "10px 16px",
    borderRadius: "8px",
    cursor: "pointer",
    fontWeight: "bold",
  },
};

export default CharacterList;
