import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import RickandMorty from "../../images/rick-and-morty.png";
import ErrorMessage from "../ErrorMessage";
import "./styles.css";

const Login = () => {
  const [loginInfo, setLoginInfo] = useState({
    username: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState("");

  const navigate = useNavigate();

  const handleChange = (event) => {
    setLoginInfo((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  };

  const loginUser = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER_URL}/login`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(loginInfo),
        }
      );
      const data = await response.json();

      if (response.ok) {
        console.log(
          "User logged in successfully. You can now see Picke Rick!!"
        );
        if (data.token) {
          localStorage.setItem("token", data.token);
        }
        navigate("/characters");
        setLoginInfo({ username: "", password: "" });
      } else {
        setErrorMessage(
          data.message || "Login failed. Please check your credentials."
        );
        setLoginInfo({ username: "", password: "" });
      }
    } catch (error) {
      console.error("Sign in error:", error);
    }
  };

  return (
    <div className="container">
      <img
        className="imageHeader"
        alt="rick and morty wiki"
        src={RickandMorty}
      />
      <h1>To see Pickle Rick you need to sign in</h1>
      <form className="form" style={styles.form} onSubmit={loginUser}>
        <input
          name="username"
          placeholder="Username"
          value={loginInfo.username}
          onChange={handleChange}
          className="input"
          required
        />
        <input
          name="password"
          placeholder="Password"
          type="password"
          value={loginInfo.password}
          onChange={handleChange}
          className="input"
          required
        />
        {errorMessage && <ErrorMessage error={errorMessage} />}
        <button className="submitButton" type="submit">
          Sign In
        </button>
      </form>
      <p className="redirectText">
        Don't have an account? Go to
        <span
          className="redirectLink"
          onClick={() => {
            navigate("/signup");
          }}
        >
          Sign up
        </span>
      </p>
    </div>
  );
};

const styles = {
  form: {
    backgroundColor: "#1f1f1f",
  },
};

export default Login;
