import React, { useState } from "react";
import fallBackImage from "../../images/morty-no-image-found.png";

const CharacterTile = ({ character, isFavorite, handleFavorites }) => {
  const [expanded, setExpanded] = useState(false);
  const {
    name,
    status,
    image,
    species,
    type,
    origin: { name: originName },
    location: { name: locationName },
    gender,
  } = character;

  return (
    <div style={styles.tile}>
      <img
        style={styles.image}
        src={image}
        alt={name}
        onError={(e) => {
          e.target.onerror = null;
          e.target.src = fallBackImage;
        }}
      />
      <h1 style={styles.name}>{name}</h1>
      <div style={styles.buttonsContainer}>
        <button style={styles.buttons} onClick={handleFavorites}>
          {isFavorite ? "Remove from Favorite" : "Add to Favorite"}
        </button>
        <button
          style={styles.buttons}
          onClick={() => {
            setExpanded(!expanded);
          }}
        >
          {expanded ? "Hide Details" : "Show Details"}
        </button>
      </div>
      {expanded && (
        <div style={styles.details}>
          <p>Status: {status}</p>
          <p>Species: {species}</p>
          <p>Type: {type || "Unknown"}</p>
          <p>Gender: {gender}</p>
          <p>Origin: {originName}</p>
          <p>Location: {locationName}</p>
        </div>
      )}
    </div>
  );
};

const styles = {
  tile: {
    border: "2px solid #02afc5",
    borderRadius: "8px",
    padding: "30px",
    height: "fit-content",
    width: "250px",
    overflow: "hidden",
  },
  name: {
    color: "#fff",
    whiteSpace: "nowrap",
    overflow: "hidden",
    textOverflow: "ellipsis",
  },
  image: {
    width: "100%",
    objectFit: "cover",
  },
  buttonsContainer: {
    display: "flex",
    justifyContent: "space-between",
    gap: "10px",
  },
  buttons: {
    background: "linear-gradient(145deg, #02afc5, #00e1ff)",
    color: "#fff",
    fontWeight: "bold",
    padding: "10px 15px",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "14px",
  },
  details: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    marginTop: "10px",
    borderRadius: "6px",
  },
};

export default CharacterTile;
