import React from "react";

const ErrorMessage = ({ error }) => {
  return <div style={styles.errorMessage}>{error}</div>;
};

const styles = {
  errorMessage: {
    color: "red",
    backgroundColor: "#ffe6e6",
    padding: "10px",
    borderRadius: "4px",
    marginTop: "10px 0 15px 0",
    fontWeight: "bold",
  },
};

export default ErrorMessage;
