import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import RickandMorty from "../images/rick-and-morty.png";
import CharacterTile from "./CharacterTile";
import Pagination from "./Pagination";

const CharacterList = () => {
  const [characters, setCharacters] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  console.log("pageNumber", pageNumber);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const apiURL = `http://localhost:5000/characters/?page=${pageNumber}`;

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        const [characterResponse, favoritesResponse] = await Promise.all([
          fetch(apiURL, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
          fetch(`http://localhost:5000/favorites/${id}`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
        ]);

        const dataCharacters = await characterResponse.json();
        const dataFavorites = await favoritesResponse.json();
        setCharacters(dataCharacters.characters);
        setFavorites(dataFavorites.favorites);
      } catch (error) {}
    };

    fetchData();

    // const getCharacters = async () => {
    //   try {
    //     const response = await fetch(apiURL, {
    //       headers: {
    //         Authorization: `Bearer ${token}`,
    //       },
    //     });
    //     const data = await response.json();
    //     setCharacters(data.characters);
    //     setTotalPages(data.info.pages);
    //   } catch (error) {
    //     console.log("There has been an error");
    //   }
    // };

    // getCharacters();
  }, [pageNumber, token]);

  const handleFavorites = async (id) => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(`http://localhost:5000/favorites/${id}`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      console.log("Favorite toggle result:", data);

      setFavorites((prev) =>
        prev.includes(id) ? prev.filter((favId) => favId !== id) : [...prev, id]
      );
    } catch (error) {
      console.error("Failed to toggle favorite", error);
    }
  };

  return (
    <div style={styles.container}>
      <img alt="rick and morty wiki" src={RickandMorty} />
      <button
        onClick={() => {
          localStorage.removeItem("token");
          navigate("/login");
        }}
      >
        destroy token
      </button>
      <div style={styles.list}>
        {characters?.map((character, index) => (
          <CharacterTile
            character={character}
            key={index}
            isFavorite={favorites.includes(character.id)}
            handleFavorites={() => handleFavorites(character.id)}
          />
        ))}
      </div>
      <Pagination pageNumber={pageNumber} setPageNumber={setPageNumber} />
    </div>
  );
};
const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  list: {
    display: "flex",
    flexWrap: "wrap",
    gap: "20px",
    justifyContent: "center",
  },
};

export default CharacterList;
