import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import RickandMorty from "../images/rick-and-morty.png";

const Login = () => {
  const [loginInfo, setLoginInfo] = useState({
    username: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState("");

  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (token) navigate("/characters");
  }, [token]);

  const handleChange = (event) => {
    setLoginInfo((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(loginInfo),
      });
      const data = await response.json();

      if (response.ok) {
        console.log(
          "User logged in successfully. You can now see Picke Rick!!"
        );
        if (data.token) {
          localStorage.setItem("token", data.token);
        }
        navigate("/characters");
        setLoginInfo({ username: "", password: "" });
      }
    } catch (error) {
      console.error("Sign in error:", error);
    }
  };

  return (
    <div style={styles.container}>
      <img alt="rick and morty wiki" src={RickandMorty} />
      <h1 style={styles.header}>To see Pickle Rick you need to sign in</h1>
      <form style={styles.form} onSubmit={handleSubmit}>
        <input
          name="username"
          placeholder="Username"
          value={loginInfo.username}
          onChange={handleChange}
          style={styles.input}
          required
        />
        <input
          name="password"
          placeholder="Password"
          type="password"
          value={loginInfo.password}
          onChange={handleChange}
          style={styles.input}
          required
        />
        {errorMessage && <div>{errorMessage}</div>}
        <button style={styles.button} type="submit">
          Sign In
        </button>
      </form>
      <p style={styles.redirect}>
        Don't an account? Go to
        <span
          style={styles.link}
          onClick={() => {
            navigate("/signup");
          }}
        >
          Sign up
        </span>
      </p>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  header: {
    color: "#fff",
  },
  form: {
    backgroundColor: "#1f1f1f",
    padding: "30px",
    borderRadius: "12px",
    boxShadow: "0 0 10px #02afc5",
    display: "flex",
    flexDirection: "column",
    gap: "15px",
    width: "100%",
    maxWidth: "400px",
  },
  input: {
    padding: "12px",
    borderRadius: "8px",
    border: "2px solid #02afc5",
    backgroundColor: "#101010",
    color: "#fff",
    fontSize: "16px",
  },
  redirect: {
    marginTop: "20px",
    color: "#02afc5",
    fontSize: "16px",
  },
  link: {
    color: "#02afc5",
    textDecoration: "underline",
    cursor: "pointer",
    fontWeight: "bold",
    marginLeft: "5px",
  },
  button: {
    backgroundColor: "#02afc5",
    color: "#fff",
    padding: "12px",
    borderRadius: "8px",
    border: "none",
    fontWeight: "bold",
    fontSize: "16px",
    cursor: "pointer",
    transition: "background 0.2s ease-in-out",
  },
};

export default Login;
